CREATE TABLE `hdp` (
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT COMMENT '主键id自增',
  `title` varchar(255) NOT NULL DEFAULT '' COMMENT '标题',
  `cover_id` int(10) NOT NULL DEFAULT '0' COMMENT '封面id',
