INSERT INTO `document` VALUES ('8', '1', '', '马云拿100亿承包春晚，会亏本？那你就不懂了吧！', '41', '众所周知，微信是靠着春晚“摇一摇”红包起家的，这不，马云接着就承包了2016年的春节，今年，马云更是大胆放言，要花100亿，“买下”春晚，胃口不小啊。', '0', '0', '0', '0', '7', '0', '28', '0', '0', '0', '0', '0', '0', '0', '1483149390', '1483149390', '10');
